document.addEventListener('DOMContentLoaded',function(){const showMessageBtns=document.querySelectorAll('.show-message-btn');const modal=document.getElementById('messageModal');const modalBody=document.querySelector('.modal-body');const closeModal=document.querySelector('.close-modal');showMessageBtns.forEach(btn=>{btn.addEventListener('click',function(){const fullMessage=this.getAttribute('data-message');modalBody.textContent=fullMessage;modal.style.display='block'})});closeModal.addEventListener('click',function(){modal.style.display='none'});window.addEventListener('click',function(event){if(event.target===modal){modal.style.display='none'}})})