document.addEventListener('DOMContentLoaded',function(){initMatrixAnimation();initTabs();initSkillCategories();initCertificateCategories();initCertificateModal();initSkillProgressBars();initPreloader();initAnimationEffects()});function initTabs(){const tabBtns=document.querySelectorAll('.tab-btn');const tabContents=document.querySelectorAll('.tab-content');tabBtns.forEach(btn=>{btn.addEventListener('click',function(){tabBtns.forEach(b=>b.classList.remove('active'));tabContents.forEach(c=>c.classList.remove('active'));this.classList.add('active');const tabId=this.getAttribute('data-tab');document.getElementById(tabId+'-tab').classList.add('active')})})}
function initSkillCategories(){const skillCategories=document.querySelectorAll('.skill-category');skillCategories.forEach(category=>{const header=category.querySelector('.skill-category-header');header.addEventListener('click',function(e){e.stopPropagation();const isActive=category.classList.contains('active');skillCategories.forEach(otherCategory=>{otherCategory.classList.remove('active')});if(!isActive){category.classList.add('active')}})})}
function initCertificateCategories(){const certificateCategories=document.querySelectorAll('.certificate-category');certificateCategories.forEach(category=>{const header=category.querySelector('.certificate-category-header');header.addEventListener('click',function(e){e.stopPropagation();const isActive=category.classList.contains('active');certificateCategories.forEach(otherCategory=>{otherCategory.classList.remove('active')});if(!isActive){category.classList.add('active')}})})}
function initCertificateModal(){const modal=document.getElementById('certificate-modal');const modalImg=document.getElementById('certificate-modal-image');const closeBtn=document.querySelector('.close-certificate-modal');const certificateImages=document.querySelectorAll('.certificate-image');certificateImages.forEach(img=>{img.addEventListener('click',function(e){e.stopPropagation();modal.classList.add('show');modalImg.src=this.src;modalImg.alt=this.alt;document.body.style.overflow='hidden'})});closeBtn.addEventListener('click',function(){modal.classList.remove('show');document.body.style.overflow='auto'});modal.addEventListener('click',function(e){if(e.target===modal){modal.classList.remove('show');document.body.style.overflow='auto'}});document.addEventListener('keydown',function(e){if(e.key==='Escape'&&modal.classList.contains('show')){modal.classList.remove('show');document.body.style.overflow='auto'}})}
function initSkillProgressBars(){const skillProgressBars=document.querySelectorAll('.skill-progress');function isInViewport(element){const rect=element.getBoundingClientRect();return(rect.top>=0&&rect.left>=0&&rect.bottom<=(window.innerHeight||document.documentElement.clientHeight)&&rect.right<=(window.innerWidth||document.documentElement.clientWidth))}
function animateProgressBars(){skillProgressBars.forEach(bar=>{if(isInViewport(bar)&&!bar.classList.contains('animated')){const percentage=bar.getAttribute('data-percentage');bar.style.width=percentage+'%';bar.classList.add('animated');const parent=bar.parentElement.parentElement;const percentageElement=parent.querySelector('.skill-percentage');if(percentageElement){let startValue=0;const endValue=parseInt(percentage);const duration=1500;const increment=Math.ceil(endValue/(duration/15));const counter=setInterval(()=>{startValue+=increment;if(startValue>endValue){startValue=endValue;clearInterval(counter)}
percentageElement.textContent=`${startValue}%`},15)}}})}
animateProgressBars();window.addEventListener('scroll',animateProgressBars);const skillCategoryHeaders=document.querySelectorAll('.skill-category-header');skillCategoryHeaders.forEach(header=>{header.addEventListener('click',function(){setTimeout(animateProgressBars,500)})})}
function initAnimationEffects(){const skillCategories=document.querySelectorAll('.skill-category');skillCategories.forEach((category,index)=>{category.style.animationDelay=`${index * 0.1}s`;category.classList.add('float-animation')});const skillItems=document.querySelectorAll('.skill-item');skillItems.forEach(item=>{item.addEventListener('mouseenter',function(){const progressBar=this.querySelector('.skill-progress');if(progressBar){progressBar.classList.add('glow-effect')}});item.addEventListener('mouseleave',function(){const progressBar=this.querySelector('.skill-progress');if(progressBar){progressBar.classList.remove('glow-effect')}})});const certificateImages=document.querySelectorAll('.certificate-image');certificateImages.forEach(img=>{img.addEventListener('mouseenter',function(){this.classList.add('pulse-animation')});img.addEventListener('mouseleave',function(){this.classList.remove('pulse-animation')})});const tabButtons=document.querySelectorAll('.tab-btn');tabButtons.forEach(btn=>{btn.addEventListener('mouseenter',function(){this.classList.add('particle-effect')});btn.addEventListener('mouseleave',function(){this.classList.remove('particle-effect')})})}
document.addEventListener('DOMContentLoaded',function(){const hamburger=document.querySelector('.hamburger');const navLinks=document.querySelector('.nav-links');hamburger.addEventListener('click',function(){navLinks.classList.toggle('active');hamburger.classList.toggle('active')})})